  const order = await get_or_create(params.cart_id, params.company_id); // insert the item to the order item table

  const orderItem = order.$relatedQuery("items").insert({
    item_id: params.product_id,
    order: order.id,
    quantity: params.quantity
  });
  return orderItem;
}

async function updateOrderItem(id, params) {
  const order = await getOrder(id);
  let orderItem; // check item quantity

  if (params.item.quantity <= 0) {
    // remove from cart
    orderItem = await order.$relatedQuery("items").delete().where({
      item_id: params.item.product_id
    });
    return orderItem;
  }

  orderItem = await order.$relatedQuery("items").patch({
    quantity: params.item.quantity
  }).where({
    item_id: params.item.product_id
  }).returning("*");
  return orderItem;
}

