function create(req, res, next) {
  req.body.owner_id = req.user.id;
  companyService.create(req.body).then(company => res.json(company)).catch(next);
}

function getAllCompanies(req, res, next) {
  companyService.getAllCompanies().then(companies => {
    res.json(scopedItems(req.user, companies));
  }).catch(next);
}

function getMyCompanies(req, res, next) {
  companyService.getMyCompanies(req.user.id).then(companies => companies ? res.json(companies) : res.sendStatus(404)).catch(next);
}

function getCompanyById(req, res, next) {
  // owner can get his company and the admin can get any company
  companyService.getCompanyById(req.params.id).then(company => {
    if (company.owner_id !== req.user.id && req.user.role !== Role.admin) {
      return res.status(401).json({
        message: "Unauthorized"
      });
    }

    res.json(company);
  }).catch(next);
}

function update(req, res, next) {
  // only owner can update their company
  companyService.updateCompany({
    id: req.params.id,
    owner_id: req.user.id
  }, req.body).then(company => res.json(company)).catch(next);
}

function _deleteCompany(req, res, next) {
  // only owner delete can delete their company
  companyService._delete({
    id: parseInt(req.params.id),
    owner_id: req.user.id
  }).then(() => {
    res.json({
      id: parseInt(req.params.id)
    });
  }).catch(next);
}
