      accounts: {
        relation: Model.HasManyRelation,
        modelClass: Account,
        join: {
          from: `${tableNames.company}.id`,
          to: `${tableNames.accounts}.company_id`
        }
      },
      menus: {
        relation: Model.HasManyRelation,
        modelClass: Menu,
        join: {
          from: `${tableNames.company}.id`,
          to: `${tableNames.menu}.company_id`
        }
      }
    };
  }

}

module.exports = Company;
