const User = require("../user/user.model");

const jwt = require("../../utils/jwt");

const bcrypt = require("bcrypt");

const crypto = require("crypto");

const sendEmail = require("../../utils/email");

module.exports = {
  /**
   * refreshtoken
   * revoketoken
   * forgotPassword
   * validateResetToken
   * validateResetToken
   * resetPassword
   */
  login,
  register,
  verifyEmail,
  create,
  update,
  getAll,
  getById,
  delete: _delete
};

async function login({
  email,
  password
}) {
  const account = await getAccount({
    email
  });

  if (!account || // !account.isVerified ||
  !(await bcrypt.compare(password, account.password))) {
    const error = new Error("Email or password is incorrect");
    throw error;
  }

  const token = await jwt.sign(account.toJSON());
  return {
    user: basicDetails(account),
    token
  };
}

async function register(params, origin) {
  // validate
  if (await getAccount({
    email: params.email
  })) {
    // send already registered error in email to prevent account enumeration
    // await sendAlreadyRegisteredEmail(params.email, origin);
    const error = new Error('Email "' + params.email + '" is already registered');
    throw error;
  }

  const account = await insertUser(params); // send email;
  // await sendVerificationEmail(account, origin);

  const token = await jwt.sign(account);
  return {
    user: account,
    token
  };
}

async function verifyEmail({
  token
}) {
  const account = await getAccount({
    verificationToken: token
  });
  if (!account) throw "Verification failed";
  await account.$query().patch({
    verified: Date.now(),
    isVerified: true,
    verificationToken: null
  });
}

async function create(params) {
  // validate
  if (await getAccount({
    email: params.email
  })) {
    throw 'Email "' + params.email + '" is already registered';
  }

  const account = await insertUser(params); // TODO? bind to company here.

  return basicDetails(account);
}

async function update(id, params) {
  const account = await getAccount({
    id
  }); // validate if email was changed

  if (params.email && account.email !== params.email && (await getAccount({
    email: params.email
  }))) {
    const error = new Error(`Email ${params.email} is already taken`);
    throw error;
  } // hash password if it was entered


  if (params.password) {
    params.password = await hash(params.password);
  }

  const updatedUser = await User.query().patchAndFetchById(id, { ...params
  });
  return basicDetails(updatedUser);
} // TODO MAKE SO IT CAN QUERY FOR DIFFERENT TYPES OF USERS


async function getAll() {
  const accounts = await User.query();
  return accounts.map(x => basicDetails(x));
}

async function getById(id) {
  const account = await getAccount({
    id
