        .catch(next);
}

function deleteItem(req, res, next) {
    const id = parseInt(req.params.id);
    itemService
        .deleteItem({ id })
        .then(() =>
            res.json({
                message: "Item deleted successfully",
                id,
            })
        )
        .catch(next);
